mapper.insert:
INSERT
  INTO MEMBER
  	(
  		M_NO
  	  , ID
  	  , PWD
  	  , NAME
  	  , PHONE
  	  , EMAIL
  	  , ADDRESS
  	  , HINT_NO
  	  , HINT_A
  	  , M_ENROLL_DATE
  	  , M_MODIFY_DATE
  	  , PWD_CHANGED_DATE
  	  , PWD_EXP_DATE
  	)
VALUES
	(
		SEQ_M_NO.NEXTVAL
	  , #{id}
	  , #{pwd}
	  , #{name}
	  , #{phone}
	  , #{email}
	  , #{address}
	  , #{hintNo}
	  , #{hintA}
	  , DEFAULT
	  , DEFAULT
	  , DEFAULT
	  , TO_DATE(ADD_MONTHS(SYSDATE, 3), 'YYYY-MM-DD')
	)

mapper.update:
UPDATE MEMBER
   SET LOGIN_FAILED_COUNT = LOGIN_FAILED_COUNT + 1
 WHERE ID = #{userId}

UPDATE MEMBER
   SET ACC_LOCK_YN = 'Y'
 WHERE ID = #{userId}

UPDATE MEMBER
   SET LOGIN_FAILED_COUNT = DEFAULT
 WHERE ID = #{userId}

mapper.select:
SELECT
		ID
  FROM MEMBER
 WHERE ID = #{id}
   AND HINT_NO = #{hintNo}
   AND HINT_A = #{hintA}
   AND ACC_SECESSION_YN = 'N'

mapper.update:
UPDATE MEMBER
   SET PHONE = #{phone}
   	 , EMAIL = #{email}
   	 , ADDRESS = #{address}
   	 , M_MODIFY_DATE = SYSDATE
   	 , HINT_NO = #{hintNo}
   	 , HINT_A = #{hintA}
 WHERE M_NO = #{mno}
